// Name: Number Utilities
// ID: numberUtilities
// Description: Adds blocks for number formatting and manipulation using JavaScript number functions
// By: MubiLop

(function(Scratch) {
  'use strict';

  if (!Scratch.extensions.unsandboxed) {
    throw new Error('"Number Utilities" cannot run unsandboxed.');
  }

  const { BlockType, ArgumentType, MenuType, Cast } = Scratch;

  const LANGUAGE_CODES = {
    'system': 'System Default',
    'en-US': 'English (US)',
    'en-GB': 'English (UK)',
    'es-ES': 'Spanish (Spain)',
    'es-MX': 'Spanish (Mexico)',
    'fr-FR': 'French',
    'de-DE': 'German',
    'it-IT': 'Italian',
    'pt-BR': 'Portuguese (Brazil)',
    'pt-PT': 'Portuguese (Portugal)',
    'ru-RU': 'Russian',
    'ja-JP': 'Japanese',
    'ko-KR': 'Korean',
    'zh-CN': 'Chinese (Simplified)',
    'zh-TW': 'Chinese (Traditional)',
    'ar-SA': 'Arabic',
    'hi-IN': 'Hindi',
    'tr-TR': 'Turkish',
    'nl-NL': 'Dutch',
    'pl-PL': 'Polish',
    'vi-VN': 'Vietnamese',
    'th-TH': 'Thai',
    'sv-SE': 'Swedish',
    'da-DK': 'Danish',
    'fi-FI': 'Finnish',
    'no-NO': 'Norwegian',
    'cs-CZ': 'Czech',
    'el-GR': 'Greek',
    'he-IL': 'Hebrew',
    'hu-HU': 'Hungarian',
    'ro-RO': 'Romanian'
  };

  const CURRENCY_CODES = {
    'USD': 'US Dollar',
    'EUR': 'Euro',
    'GBP': 'British Pound',
    'JPY': 'Japanese Yen',
    'CNY': 'Chinese Yuan',
    'KRW': 'South Korean Won',
    'INR': 'Indian Rupee',
    'RUB': 'Russian Ruble',
    'BRL': 'Brazilian Real',
    'MXN': 'Mexican Peso',
    'CAD': 'Canadian Dollar',
    'AUD': 'Australian Dollar',
    'NZD': 'New Zealand Dollar',
    'CHF': 'Swiss Franc',
    'SEK': 'Swedish Krona',
    'NOK': 'Norwegian Krone',
    'DKK': 'Danish Krone',
    'SGD': 'Singapore Dollar',
    'HKD': 'Hong Kong Dollar',
    'AED': 'UAE Dirham',
    'SAR': 'Saudi Riyal',
    'ZAR': 'South African Rand',
    'TRY': 'Turkish Lira',
    'PLN': 'Polish ZÅ‚oty',
    'THB': 'Thai Baht',
    'IDR': 'Indonesian Rupiah',
    'PHP': 'Philippine Peso',
    'MYR': 'Malaysian Ringgit',
    'VND': 'Vietnamese Dong',
    'EGP': 'Egyptian Pound'
  };
  
  const ico = "data:image/svg+xml;base64,";
  class MubiLopNumberUtilities {
    constructor() {
      // system locale
      this.systemLocale = navigator.language || 'en-US';
    }

    getInfo() {
      return {
        id: 'numberUtilities',
        name: 'Number Utilities',
        menuIconURI: ico,
        blockIconURI: ico,
        color1: '#59c059',
        color2: '#46a346',
        color3: '#389438',
        menus: {
          locales: {
            items: Object.entries(LANGUAGE_CODES).map(([code, name]) => ({ text: name, value: code }))
          },
          currencies: {
            items: Object.entries(CURRENCY_CODES).map(([code, name]) => ({ text: `${code} - ${name}`, value: code }))
          }
        },
        blocks: [
          {
            opcode: 'toLocaleString',
            blockType: BlockType.REPORTER,
            text: 'format number [NUMBER] with locale [LOCALE]',
            arguments: {
              NUMBER: {
                type: ArgumentType.NUMBER,
                defaultValue: 10000
              },
              LOCALE: {
                type: ArgumentType.STRING,
                menu: 'locales',
                defaultValue: 'system'
              }
            }
          },
          {
            opcode: 'toFixed',
            blockType: BlockType.REPORTER,
            text: 'format [NUMBER] with [DECIMALS] decimal places',
            arguments: {
              NUMBER: {
                type: ArgumentType.NUMBER,
                defaultValue: 3.14159
              },
              DECIMALS: {
                type: ArgumentType.NUMBER,
                defaultValue: 2
              }
            }
          },
          {
            opcode: 'toPrecision',
            blockType: BlockType.REPORTER,
            text: 'format [NUMBER] with [PRECISION] significant figures',
            arguments: {
              NUMBER: {
                type: ArgumentType.NUMBER,
                defaultValue: 3.14159
              },
              PRECISION: {
                type: ArgumentType.NUMBER,
                defaultValue: 3
              }
            }
          },
          {
            opcode: 'toExponential',
            blockType: BlockType.REPORTER,
            text: 'convert [NUMBER] to exponential notation with [DECIMALS] decimals',
            arguments: {
              NUMBER: {
                type: ArgumentType.NUMBER,
                defaultValue: 1234.5678
              },
              DECIMALS: {
                type: ArgumentType.NUMBER,
                defaultValue: 2
              }
            }
          },
          {
            opcode: 'formatCurrency',
            blockType: BlockType.REPORTER,
            text: 'format [NUMBER] as currency in [CURRENCY]',
            arguments: {
              NUMBER: {
                type: ArgumentType.NUMBER,
                defaultValue: 123.45
              },
              CURRENCY: {
                type: ArgumentType.STRING,
                menu: 'currencies',
                defaultValue: 'USD'
              }
            }
          },
          {
            opcode: 'formatPercentage',
            blockType: BlockType.REPORTER,
            text: 'format [NUMBER] as percentage with [DECIMALS] decimals',
            arguments: {
              NUMBER: {
                type: ArgumentType.NUMBER,
                defaultValue: 0.8547
              },
              DECIMALS: {
                type: ArgumentType.NUMBER,
                defaultValue: 1
              }
            }
          },
          {
            opcode: 'roundNumber',
            blockType: BlockType.REPORTER,
            text: 'round [NUMBER] to nearest [MULTIPLE]',
            arguments: {
              NUMBER: {
                type: ArgumentType.NUMBER,
                defaultValue: 127
              },
              MULTIPLE: {
                type: ArgumentType.NUMBER,
                defaultValue: 10
              }
            }
          },
          {
            opcode: 'isInteger',
            blockType: BlockType.BOOLEAN,
            text: 'is [NUMBER] an integer?',
            arguments: {
              NUMBER: {
                type: ArgumentType.NUMBER,
                defaultValue: 5
              }
            }
          },
          {
            opcode: 'isFinite',
            blockType: BlockType.BOOLEAN,
            text: 'is [NUMBER] finite?',
            arguments: {
              NUMBER: {
                type: ArgumentType.NUMBER,
                defaultValue: 42
              }
            }
          },
          {
            opcode: 'clamp',
            blockType: BlockType.REPORTER,
            text: 'clamp [NUMBER] between [MIN] and [MAX]',
            arguments: {
              NUMBER: {
                type: ArgumentType.NUMBER,
                defaultValue: 50
              },
              MIN: {
                type: ArgumentType.NUMBER,
                defaultValue: 0
              },
              MAX: {
                type: ArgumentType.NUMBER,
                defaultValue: 100
              }
            }
          }
        ]
      };
    }

    toLocaleString(args) {
      const locale = Object.keys(LANGUAGE_CODES)
        .find(v => v == args.LOCALE && v != 'system') || this.systemLocale;
      return Cast.toNumber(args.NUMBER).toLocaleString(locale);
    }

    toFixed(args) {
      return Cast.toNumber(args.NUMBER).toFixed(Cast.toNumber(args.DECIMALS));
    }

    toPrecision(args) {
      return Cast.toNumber(args.NUMBER).toPrecision(Cast.toNumber(args.PRECISION));
    }

    toExponential(args) {
      return Cast.toNumber(args.NUMBER).toExponential(Cast.toNumber(args.DECIMALS));
    }

    formatCurrency(args) {
      try {
        return Cast.toNumber(args.NUMBER).toLocaleString('en-US', {
          style: 'currency',
          currency: args.CURRENCY
        });
      } catch (e) {
        return Cast.toNumber(args.NUMBER); // eh
      }
    }

    formatPercentage(args) {
      const number = Cast.toNumber(args.NUMBER);
      return `${(number * 100).toFixed(Cast.toNumber(args.DECIMALS))}%`;
    }

    roundNumber(args) {
      const multiple = Cast.toNumber(args.MULTIPLE);
      return Math.round(Cast.toNumber(args.NUMBER) / multiple) * multiple;
    }

    isInteger(args) {
      return Number.isInteger(Cast.toNumber(args.NUMBER));
    }

    isFinite(args) {
      return Number.isFinite(Cast.toNumber(args.NUMBER));
    }

    clamp(args) {
      const num = Cast.toNumber(args.NUMBER);
      let min = Cast.toNumber(args.MIN);
      let max = Cast.toNumber(args.MAX);
      
      // swap stuff
      if (min > max) {
        [min, max] = [max, min];
      }
      
      return Math.min(Math.max(num, min), max);
    }
  }

  Scratch.extensions.register(new MubiLopNumberUtilities());
})(Scratch);
