// Name: Pause Utilities
// ID: SPPause
// Description: Pause the Project and certain Scripts
// By: SharkPool <https://github.com/SharkPool-SP>

// Version V.1.0.0

(function (Scratch) {
  "use strict";

  if (!Scratch.extensions.unsandboxed) {
    alert("Pause Utilities Extension must run unsandboxed!");
  }
  const runtime = Scratch.vm.runtime;
  let storedScripts = {};

  const menuIconURI =
"data:image/svg+xml;base64,";

  const blockIconURI =
"data:image/svg+xml;base64,";

  class SPPause {
    getInfo() {
      return {
        id: "SPPause",
        name: "Pause Utilities",
        color1: "#5f5b49",
        menuIconURI,
        blockIconURI,
        blocks: [
          {
            blockType: Scratch.BlockType.LABEL,
            text: window.location.href.includes("penguinmod") ? "Manual Blocks" : "Manual Block",
          },
          {
            opcode: "pause",
            blockType: Scratch.BlockType.COMMAND,
            text: "pause project",
          },
          {
            opcode: "unpause",
            blockType: Scratch.BlockType.COMMAND,
            text: "unpause project",
            hideFromPalette: !window.location.href.includes("penguinmod"),
          },
          {
            blockType: Scratch.BlockType.LABEL,
            text: "Automatic Blocks",
          },
          {
            opcode: "pauseLoop",
            blockType: Scratch.BlockType.COMMAND,
            text: "pause this script with ID [NAME]",
            arguments: {
              NAME: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "my script",
              },
            }
          },
          {
            opcode: "breakLoop",
            blockType: Scratch.BlockType.COMMAND,
            text: "unpause script with ID [NAME]",
            arguments: {
              NAME: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "my script",
              },
            }
          },
          {
            opcode: "isPaused",
            blockType: Scratch.BlockType.BOOLEAN,
            text: "is script with ID [NAME] paused?",
            arguments: {
              NAME: {
                type: Scratch.ArgumentType.STRING,
                defaultValue: "my script",
              },
            }
          },
        ],
      };
    }

    pause() {
      if (window.location.href.includes("penguinmod")) {
        runtime.pause();
      } else {
        const pauseButton = document.querySelector("img.pause-btn.addons-display-none-pause");
        if (pauseButton) {
          pauseButton.click();
        } else {
          console.log("Pause button not found.");
        }
      }
    }

    unpause() { //penguinmod exlusive
      runtime.play();
    }

    pauseLoop(args) {
      storedScripts[args.NAME] = [false];
      return new Promise((resolve) => {
        const checkStatus = () => {
          if (storedScripts[args.NAME][0] === true) {
            resolve();
          } else {
            setTimeout(checkStatus, 100);
          }
        };
        checkStatus();
      });
    }

    breakLoop(args) {
      if (storedScripts[args.NAME]) {
        storedScripts[args.NAME][0] = true;
      }
    }

    isPaused(args) {
      if (storedScripts[args.NAME]) {
        return !storedScripts[args.NAME][0];
      }
      return false;
    }
  }

  Scratch.extensions.register(new SPPause());
})(Scratch);
