let externalWindow;
let username;
let hadError = false;
let errorText = "";
let errors = [];

function addNewError(err) {
  errors.push(err + " | " + new Date().toLocaleString())
}

//Server icon by Icons8 (https://icons8.com/) - https://icons8.com/icon/1340/server

const image = "data:image/png;base64,"

const menuimg = "data:image/png;base64,"

function openWindow(url) {
  const windowFeatures = 'width=700,height=500,top=100,left=100';

  // Open the external window
  externalWindow = window.open(url, "_blank", windowFeatures);

  // Listen for messages from the external window
  window.addEventListener("message", handleMessage);

  function handleMessage(event) {
    // Check the origin of the message to ensure it's from the expected source
    // Handle the message data sent from the external window
    const data = event.data;
    username = data;
    //externalWindow.close();
    window.removeEventListener("message", handleMessage);
    access = true
  }
}

let access = false;
class Storage {
  getInfo() {
    return {
      id: 'kokobetterstorage',
      name: 'Better Server Storage',
      menuIconURI: menuimg,
      blockIconURI: image,
      color1: '#42a9cf',
      color2: '#327f9c',
      color3: '#245c70',
      blocks: [
        {
          opcode: 'lockbutton',
          blockType: Scratch.BlockType.BUTTON,
          text: 'Reserve a key...',
        },
        {
          opcode: 'login',
          blockType: Scratch.BlockType.COMMAND,
          text: 'log in first',
        },
        {
          opcode: 'loggedbool',
          blockType: Scratch.BlockType.BOOLEAN,
          text: 'logged in?',
        },
        {
          opcode: 'errorlog',
          blockType: Scratch.BlockType.REPORTER,
          text: 'error log (array format)',
          disableMonitor: true
        },
        {
          opcode: 'errorlogclear',
          blockType: Scratch.BlockType.COMMAND,
          text: 'clear the error log'
        },
        {
          opcode: 'islocked',
          blockType: Scratch.BlockType.BOOLEAN,
          text: 'is key [KEY] reserved (read-only) [MENU]?',
          arguments: {
            KEY: {
              type: Scratch.ArgumentType.STRING,
              defaultValue: 'scratch'
            },
            MENU: {
              type: Scratch.ArgumentType.STRING,
              menu: 'BOOLEANLOCK'
            }
          }
        },
        "---",
        {
          opcode: 'haderror',
          blockType: Scratch.BlockType.BOOLEAN,
          text: 'server error?',
        },
        {
          opcode: 'errortext',
          blockType: Scratch.BlockType.REPORTER,
          text: 'error text',
        },
        {
          opcode: 'savetostorage',
          blockType: Scratch.BlockType.COMMAND,
          text: 'save key [KEY] and value [VALUE] to server',
          arguments: {
            KEY: {
              type: Scratch.ArgumentType.STRING,
              defaultValue: 'hello'
            },
            VALUE: {
              type: Scratch.ArgumentType.STRING,
              defaultValue: 'world!'
            }
          }
        },
        {
          opcode: 'getfromstorage',
          blockType: Scratch.BlockType.REPORTER,
          text: 'get value of key [KEY] from server',
          arguments: {
            KEY: {
              type: Scratch.ArgumentType.STRING,
              defaultValue: 'scratch'
            }
          }
        }
      ],
      menus: {
        BOOLEANLOCK: {
          acceptReporters: true,
          items: ['for everyone else', 'for me']
        }
      }
    };
  }

  savetostorage(args) {
    if (access) {
      fetch("https://penguinmod-data.kokoiscool.repl.co/set.php?key=" + encodeURIComponent(args.KEY) + "&value=" + encodeURIComponent(args.VALUE) + "&username=" + encodeURIComponent(username), {
        method: 'GET'
      })
        .then((response) => {
          if (!response.ok) {
            hadError = true;
            return response.text().then((errorContent) => {
              errorText = errorContent;
              addNewError(errorContent)
            });
          }
          hadError = false;
          errorText = "";
          return response.text();
        })
        .catch((error) => {
          console.error(error);
          hadError = true;
          errorText = error;
          addNewError(error)
          return 'Uh oh! Something went wrong.';
        });
    } else {
      alert("You are not logged in.");
    }
  }
  getfromstorage(args) {
    return fetch("https://penguinmod-data.kokoiscool.repl.co/get.php?key=" + encodeURIComponent(args.KEY), {
      method: 'GET'
    })
      .then((response) => {
          if (!response.ok) {
            hadError = true;
            return response.text().then((errorContent) => {
              errorText = errorContent;
              addNewError(errorContent)
            });
          }
          hadError = false;
          errorText = "";
          return response.text();
       })
      .catch((error) => {
        console.error(error);
        hadError = true
        errorText = error
        addNewError(error)
        return 'Uh oh! Something went wrong.';
      });
  }
  login() {
    openWindow("https://storage-auth-python.kokoiscool.repl.co/scratchauth")
  }
  haderror() {
    return hadError;
  }
  errortext() {
    return errorText;
  }
  errorlog() {
    return JSON.stringify(errors)
  }
  loggedbool() {
    return access
  }
  errorlogclear() {
    errors = []
  }
  islocked(args) {
    if (access) {
      var url;
      if (args.MENU == "for everyone else") {
        url = "https://penguinmod-data.kokoiscool.repl.co/islocked.php?key=" + encodeURIComponent(args.KEY)
      } else {
        url = "https://penguinmod-data.kokoiscool.repl.co/islocked.php?key=" + encodeURIComponent(args.KEY) + "&username=" + encodeURIComponent(username)
      }
      return fetch(url, {
        method: 'GET'
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          return response.json();
        })
        .then((data) => {
          if (data.result !== undefined) {
            return data.result;
          } else {
            throw new Error('The "result" property was not found in the JSON response');
          }
        })
        .catch((error) => {
          console.error(error);
          addNewError(error)
          return 'Uh oh! Something went wrong.';
        });
    } else {
      alert("You are not logged in.")
    }
  }
  lockbutton() {
    window.open('https://forms.gle/hQ5Qiq1gtkppR1Fj7', '_blank');
  }
}
Scratch.extensions.register(new Storage());
